jQuery(document).ready(function($) { // Start of DOM ready

	// fading pages
	$('.primary li a, a.project-block[target!="_blank"], .brick, .paging-link a, a.next-post').not('a.nofade').click(function(e) {
	    e.preventDefault();
		$('body').removeClass('nav-open');
	   	$('body').addClass('moving');
	   	$('.loader').removeClass('hide');
	    var href = $(this).attr('href');
     	// Delay setting the location for 300ms
		setTimeout(function() {window.location = href}, 300);
		return false;
	});

	$('#site-content .layout-container').addClass('fadeIn');

    $('.loader').addClass('hide');

	$('.contact-text-block').matchHeight();
	$('.c-project__description').matchHeight();

	// hamburger time - bind switch mode

	function fireNav() {
	    $('body').toggleClass('nav-open');
	}

	$('.hamburger').on("click", fireNav);


	// responsive tabs on about page
	$('.skills').responsiveTabs({
    	startCollapsed: 'accordion'
	});

	var $grid = $('.post-wall').imagesLoaded( function() {
		// init Isotope after all images have loaded
		$grid.isotope({
			itemSelector: '.brick',
			percentPosition: true,
			masonry: {
				// use element for option
				columnWidth: '.brick'
			}
		}, function(){
			console.log('masonry done');
		});

		onScrollInit( $('.brick'));

	});
	var $distrakt = $('.distrakt-page').imagesLoaded( function() {
		// init Isotope after all images have loaded
		$distrakt.isotope({
			itemSelector: '.pin',
			percentPosition: true,
			masonry: {
				// use element for option
				columnWidth: '.pin'
			}
		}, function(){
			console.log('masonry done');
		});

		onScrollInit( $('.pin'));

	});

	// post search toggle
	$('.search-toggle').click(function(e) {
	    e.preventDefault();
		$('.search-form').toggleClass('hide');
		$( ".search-form input" ).focus();
	});


}); // End DOM ready


